const formErrorsReducer = (state = {}, action) => {
    switch(action.type) {
        case 'SET_ERRORS': {
            return {...action.payload}
        }
        case 'CLEAR_ERRORS': {
            return {}
        }
        default: {
            return {...state}
        }
    }
}

export default formErrorsReducer
